CREATE TABLE `product_table` (
  `PRODUCT_ID` int(11) NOT NULL AUTO_INCREMENT,
  `PRODUCT_BRAND` varchar(20) DEFAULT NULL,
  `PRODUCT_NAME` varchar(20) DEFAULT NULL,
  `PRODUCT_SXWX` varchar(20) DEFAULT NULL,
  `PRODUCT_DATE` varchar(20) DEFAULT NULL,
  `PRODUCT_WEIGHT` varchar(20) DEFAULT NULL,
  `PRODUCT_SERIES` varchar(20) DEFAULT NULL,
  `PRODUCT_PACKAGE` varchar(20) DEFAULT NULL,
  `MALL_PRICE` int(11) DEFAULT NULL,
  `PRODUCT_NUM` int(11) DEFAULT NULL,
  `PRODUCT_IMAGE` varchar(1000) DEFAULT NULL,
  `COST_PRICE` int(11) DEFAULT NULL,
  `SHIFOUTUIGUANG` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`PRODUCT_ID`),
  UNIQUE KEY `PRODUCT_ID` (`PRODUCT_ID`)
) ENGINE=InnoDB AUTO_INCREMENT=203 DEFAULT CHARSET=utf8;




